const HealthVisualization = () => {
  const [selectedMetrics, setSelectedMetrics] = useState({
    triglycerides: true,
    hdl: true,
    ldl: true,
    total: true
  });

  // Embed the exact data from the medical analysis
  const rawData = [
    { date: "2013-01-01", triglycerides: 426, hdl: null, ldl: 52, total: 205 },
    { date: "2014-01-01", triglycerides: 390, hdl: 25, ldl: 87, total: 223 },
    { date: "2015-01-01", triglycerides: 390, hdl: 25, ldl: 87, total: 223 },
    { date: "2016-01-01", triglycerides: 280, hdl: 32, ldl: 75, total: 188 },
    { date: "2017-01-01", triglycerides: 220, hdl: 35, ldl: 72, total: 175 },
    { date: "2018-01-01", triglycerides: 180, hdl: 38, ldl: 114, total: 180 },
    { date: "2019-01-01", triglycerides: 150, hdl: 40, ldl: 114, total: 185 },
    { date: "2020-01-01", triglycerides: 100, hdl: 42, ldl: 99, total: 161 },
    { date: "2021-01-01", triglycerides: 51, hdl: 45, ldl: 87, total: 142 },
    { date: "2022-01-01", triglycerides: 53, hdl: 47, ldl: 87, total: 144 },
    { date: "2022-11-01", triglycerides: 86, hdl: 42, ldl: 87, total: 118 },
    { date: "2023-01-01", triglycerides: 86, hdl: null, ldl: null, total: null },
    { date: "2025-01-01", triglycerides: 153, hdl: null, ldl: null, total: null }
  ];

  const latestData = {
    triglycerides: { value: 153, date: "2025", trend: 78, status: "high" },
    hdl: { value: 42, date: "Nov 2022", trend: 68, status: "low" },
    ldl: { value: 87, date: "Nov 2022", trend: 0, status: "normal" },
    total: { value: 118, date: "Nov 2022", trend: -42, status: "normal" }
  };

  const referenceRanges = {
    triglycerides: { max: 150, label: "Normal &lt; 150" },
    hdl: { min: 40, label: "Normal &gt; 40" },
    ldl: { max: 100, label: "Normal &lt; 100" },
    total: { max: 200, label: "Normal &lt; 200" }
  };

  const getStatusColor = (status) => {
    if (status === 'normal') return 'text-green-600';
    if (status === 'high') return 'text-red-600';
    if (status === 'low') return 'text-orange-600';
    return 'text-gray-600';
  };

  const getStatusBgColor = (status) => {
    if (status === 'normal') return 'bg-green-50 border-green-200';
    if (status === 'high') return 'bg-red-50 border-red-200';
    if (status === 'low') return 'bg-orange-50 border-orange-200';
    return 'bg-gray-50 border-gray-200';
  };

  const getTrendIcon = (trend) => {
    if (trend > 0) return <ArrowUp className="w-4 h-4" />;
    if (trend < 0) return <ArrowDown className="w-4 h-4" />;
    return null;
  };

  const metricColors = {
    triglycerides: "#ef4444",
    hdl: "#10b981",
    ldl: "#f59e0b",
    total: "#3b82f6"
  };

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-3 rounded-lg shadow-lg border border-gray-200">
          <p className="font-semibold text-gray-700 mb-2">{label}</p>
          {payload.map((entry, index) => (
            entry.value && (
              <div key={index} className="flex items-center gap-2 text-sm">
                <div 
                  className="w-3 h-3 rounded-full"
                  style={{ backgroundColor: entry.color }}
                />
                <span className="font-medium capitalize">{entry.dataKey}:</span>
                <span>{entry.value} mg/dL</span>
              </div>
            )
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="p-6 bg-gray-50 rounded-lg">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">
          15-Year Cholesterol Trend Analysis
        </h2>
        <p className="text-gray-600">Comprehensive view of your cholesterol metrics from 2013-2025</p>
      </div>

      {/* Current Status Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <div className={'border rounded-lg p-4 ' + getStatusBgColor(latestData.triglycerides.status)}>
          <div className="flex items-center justify-between mb-2">
            <span className="text-gray-700 font-medium">Triglycerides</span>
            <Droplet className={getStatusColor(latestData.triglycerides.status) + ' w-5 h-5'} />
          </div>
          <div className={'text-2xl font-bold mb-1 ' + getStatusColor(latestData.triglycerides.status)}>
            {latestData.triglycerides.value} mg/dL
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">{referenceRanges.triglycerides.label}</span>
            <div className={'flex items-center gap-1 ' + (latestData.triglycerides.trend > 0 ? 'text-red-600' : 'text-green-600')}>
              {getTrendIcon(latestData.triglycerides.trend)}
              <span>{Math.abs(latestData.triglycerides.trend)}%</span>
            </div>
          </div>
          <div className="text-xs text-gray-500 mt-1">Last: {latestData.triglycerides.date}</div>
        </div>

        <div className={'border rounded-lg p-4 ' + getStatusBgColor(latestData.hdl.status)}>
          <div className="flex items-center justify-between mb-2">
            <span className="text-gray-700 font-medium">HDL (Good)</span>
            <Shield className={getStatusColor(latestData.hdl.status) + ' w-5 h-5'} />
          </div>
          <div className={'text-2xl font-bold mb-1 ' + getStatusColor(latestData.hdl.status)}>
            {latestData.hdl.value} mg/dL
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">{referenceRanges.hdl.label}</span>
            <div className={'flex items-center gap-1 text-green-600'}>
              {getTrendIcon(-latestData.hdl.trend)}
              <span>{latestData.hdl.trend}%</span>
            </div>
          </div>
          <div className="text-xs text-gray-500 mt-1">Last: {latestData.hdl.date}</div>
        </div>

        <div className={'border rounded-lg p-4 ' + getStatusBgColor(latestData.ldl.status)}>
          <div className="flex items-center justify-between mb-2">
            <span className="text-gray-700 font-medium">LDL (Bad)</span>
            <AlertCircle className={getStatusColor(latestData.ldl.status) + ' w-5 h-5'} />
          </div>
          <div className={'text-2xl font-bold mb-1 ' + getStatusColor(latestData.ldl.status)}>
            {latestData.ldl.value} mg/dL
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">{referenceRanges.ldl.label}</span>
            <div className="text-gray-400"></div>
          </div>
          <div className="text-xs text-gray-500 mt-1">Last: {latestData.ldl.date}</div>
        </div>

        <div className={'border rounded-lg p-4 ' + getStatusBgColor(latestData.total.status)}>
          <div className="flex items-center justify-between mb-2">
            <span className="text-gray-700 font-medium">Total</span>
            <Activity className={getStatusColor(latestData.total.status) + ' w-5 h-5'} />
          </div>
          <div className={'text-2xl font-bold mb-1 ' + getStatusColor(latestData.total.status)}>
            {latestData.total.value} mg/dL
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-500">{referenceRanges.total.label}</span>
            <div className={'flex items-center gap-1 text-green-600'}>
              {getTrendIcon(latestData.total.trend)}
              <span>{Math.abs(latestData.total.trend)}%</span>
            </div>
          </div>
          <div className="text-xs text-gray-500 mt-1">Last: {latestData.total.date}</div>
        </div>
      </div>

      {/* Alert Box */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 mb-8">
        <div className="flex items-start gap-3">
          <AlertTriangle className="w-5 h-5 text-yellow-600 mt-0.5" />
          <div className="flex-1">
            <h4 className="font-semibold text-yellow-900 mb-1">Critical Monitoring Gap</h4>
            <p className="text-sm text-yellow-800">
              No complete cholesterol panel since November 2022 (2.5 years). Recent triglycerides increased 78% from 86 to 153 mg/dL.
              Schedule a complete lipid panel immediately.
            </p>
          </div>
        </div>
      </div>

      {/* Chart */}
      <div className="bg-white rounded-lg border border-gray-200 p-6 mb-8">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-gray-900">Cholesterol Trends Over Time</h3>
          <div className="flex gap-4">
            {Object.entries(selectedMetrics).map(([metric, isSelected]) => (
              <label key={metric} className="flex items-center gap-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={isSelected}
                  onChange={() => setSelectedMetrics(prev => ({ ...prev, [metric]: !prev[metric] }))}
                  className="rounded border-gray-300"
                />
                <span className="text-sm capitalize" style={{ color: metricColors[metric] }}>
                  {metric}
                </span>
              </label>
            ))}
          </div>
        </div>

        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={rawData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
            <XAxis 
              dataKey="date" 
              tickFormatter={(date) => new Date(date).getFullYear()}
              stroke="#6b7280"
            />
            <YAxis stroke="#6b7280" />
            <Tooltip content={<CustomTooltip />} />
            <Legend />

            {/* Reference lines */}
            <ReferenceLine y={150} stroke="#ef4444" strokeDasharray="5 5" />
            <ReferenceLine y={40} stroke="#10b981" strokeDasharray="5 5" />
            <ReferenceLine y={100} stroke="#f59e0b" strokeDasharray="5 5" />
            <ReferenceLine y={200} stroke="#3b82f6" strokeDasharray="5 5" />

            {selectedMetrics.triglycerides && (
              <Line 
                type="monotone" 
                dataKey="triglycerides" 
                stroke={metricColors.triglycerides}
                strokeWidth={2}
                dot={{ fill: metricColors.triglycerides, r: 4 }}
                connectNulls
              />
            )}
            {selectedMetrics.hdl && (
              <Line 
                type="monotone" 
                dataKey="hdl" 
                stroke={metricColors.hdl}
                strokeWidth={2}
                dot={{ fill: metricColors.hdl, r: 4 }}
                connectNulls
              />
            )}
            {selectedMetrics.ldl && (
              <Line 
                type="monotone" 
                dataKey="ldl" 
                stroke={metricColors.ldl}
                strokeWidth={2}
                dot={{ fill: metricColors.ldl, r: 4 }}
                connectNulls
              />
            )}
            {selectedMetrics.total && (
              <Line 
                type="monotone" 
                dataKey="total" 
                stroke={metricColors.total}
                strokeWidth={2}
                dot={{ fill: metricColors.total, r: 4 }}
                connectNulls
              />
            )}
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* Key Findings */}
      <div className="bg-white rounded-lg border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Key Findings</h3>
        <div className="space-y-3">
          <div className="flex items-start gap-3">
            <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
            <div>
              <div className="font-medium text-gray-900">Remarkable Recovery (2014-2021)</div>
              <div className="text-sm text-gray-600">Triglycerides dropped from life-threatening 426 mg/dL to optimal 51 mg/dL</div>
            </div>
          </div>
          <div className="flex items-start gap-3">
            <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
            <div>
              <div className="font-medium text-gray-900">Best Control Period (2021-2023)</div>
              <div className="text-sm text-gray-600">Triglycerides excellently controlled between 53-86 mg/dL</div>
            </div>
          </div>
          <div className="flex items-start gap-3">
            <AlertCircle className="w-5 h-5 text-orange-600 mt-0.5" />
            <div>
              <div className="font-medium text-gray-900">HDL Improvement But Suboptimal</div>
              <div className="text-sm text-gray-600">68% improvement from 25 to 42 mg/dL, but still below optimal levels</div>
            </div>
          </div>
          <div className="flex items-start gap-3">
            <AlertTriangle className="w-5 h-5 text-red-600 mt-0.5" />
            <div>
              <div className="font-medium text-gray-900">Recent Concerning Trend (2023-2025)</div>
              <div className="text-sm text-gray-600">Triglycerides increased 78% from 86 to 153 mg/dL, exceeding normal range</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
