const HealthVisualization = () => {
  const [selectedMetrics, setSelectedMetrics] = useState({
    hba1c: true,
    weight: true,
    metformin: true
  });

  // Embedded data extracted from medical analysis
  const rawData = [
    { date: "2021-06-01", hba1c: 5.7, weight: 193, metformin: 500 },
    { date: "2021-09-01", hba1c: 5.8, weight: 198, metformin: 500 },
    { date: "2021-12-01", hba1c: 5.8, weight: 201, metformin: 500 },
    { date: "2022-03-01", hba1c: 5.9, weight: 205, metformin: 500 },
    { date: "2022-06-01", hba1c: 5.9, weight: 208, metformin: 500 },
    { date: "2022-09-01", hba1c: 5.9, weight: 211, metformin: 500 },
    { date: "2022-12-01", hba1c: 6.0, weight: 214, metformin: 500 },
    { date: "2023-03-01", hba1c: 6.0, weight: 217, metformin: 500 },
    { date: "2023-06-01", hba1c: 6.0, weight: 219, metformin: 500 },
    { date: "2023-09-01", hba1c: 6.0, weight: 221, metformin: 500 },
    { date: "2023-12-01", hba1c: 6.1, weight: 223, metformin: 500 },
    { date: "2024-03-01", hba1c: 6.1, weight: 225, metformin: 500 },
    { date: "2024-06-01", hba1c: 6.1, weight: 226, metformin: 500 },
    { date: "2024-09-01", hba1c: 6.1, weight: 227, metformin: 500 },
    { date: "2025-01-01", hba1c: 6.1, weight: 227, metformin: 500 }
  ];

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { month: 'short', year: 'numeric' });
  };

  // Calculate changes
  const firstData = rawData[0];
  const lastData = rawData[rawData.length - 1];
  const hba1cChange = ((lastData.hba1c - firstData.hba1c) / firstData.hba1c * 100).toFixed(1);
  const weightChange = ((lastData.weight - firstData.weight) / firstData.weight * 100).toFixed(1);
  const averageHba1c = (rawData.reduce((sum, d) => sum + d.hba1c, 0) / rawData.length).toFixed(2);

  // Compute className strings before JSX
  const getMetricCardClass = (isNormal) => {
    return 'rounded-lg p-6 border-2 ' + (isNormal ? 'bg-green-50 border-green-200' : 'bg-red-50 border-red-200');
  };

  const getTrendIconClass = (value) => {
    return 'w-5 h-5 ' + (value > 0 ? 'text-red-500' : 'text-green-500');
  };

  const getHba1cStatusClass = (value) => {
    if (value <= 5.6) return 'text-green-600 bg-green-100';
    if (value <= 6.0) return 'text-yellow-600 bg-yellow-100';
    return 'text-red-600 bg-red-100';
  };

  const CustomTooltip = ({ active, payload }) => {
    if (!active || !payload) return null;

    const tooltipClass = 'bg-white p-3 rounded-lg shadow-lg border border-gray-200';
    return (
      <div className={tooltipClass}>
        <p className="font-semibold text-gray-700">{formatDate(payload[0].payload.date)}</p>
        {payload.map((entry, index) => {
          if (!selectedMetrics[entry.dataKey]) return null;
          const valueClass = entry.dataKey === 'hba1c' ? getHba1cStatusClass(entry.value) + ' px-2 py-1 rounded inline-block' : '';
          return (
            <p key={index} className="mt-1">
              <span style={{ color: entry.color }} className="font-medium">
                {entry.name}:
              </span>{' '}
              <span className={valueClass}>
                {entry.value}{entry.dataKey === 'hba1c' ? '%' : entry.dataKey === 'weight' ? ' lbs' : ' mg'}
              </span>
            </p>
          );
        })}
      </div>
    );
  };

  const toggleMetric = (metric) => {
    setSelectedMetrics(prev => ({
      ...prev,
      [metric]: !prev[metric]
    }));
  };

  const legendItemClass = (metric, isActive) => {
    return 'flex items-center gap-2 px-3 py-2 rounded-lg cursor-pointer transition-all ' + 
           (isActive ? 'bg-blue-100 border-blue-300' : 'bg-gray-100 border-gray-300') + 
           ' border hover:shadow-md';
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
            <Activity className="w-8 h-8 text-blue-600" />
            HbA1c, Metformin, and Weight Analysis
          </h2>
          <p className="text-gray-600 mt-2">
            Tracking your glycemic control, medication, and weight since starting metformin in June 2021
          </p>
        </div>

        {/* Key Metrics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* HbA1c Card */}
          <div className={getMetricCardClass(false)}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-lg font-semibold text-gray-700">HbA1c Level</h3>
                <p className="text-3xl font-bold text-gray-900 mt-2">{lastData.hba1c}%</p>
                <p className="text-sm text-gray-600 mt-1">Current (Target: 4.0-5.6%)</p>
              </div>
              <Droplet className="w-10 h-10 text-red-400" />
            </div>
            <div className="mt-4 flex items-center gap-2">
              <TrendingUp className={getTrendIconClass(hba1cChange)} />
              <span className="text-sm font-medium text-red-600">+{hba1cChange}% since start</span>
            </div>
            <div className="mt-2 text-sm text-gray-600">
              Average: {averageHba1c}% (Above normal)
            </div>
          </div>

          {/* Weight Card */}
          <div className={getMetricCardClass(false)}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-lg font-semibold text-gray-700">Weight</h3>
                <p className="text-3xl font-bold text-gray-900 mt-2">{lastData.weight} lbs</p>
                <p className="text-sm text-gray-600 mt-1">Current weight</p>
              </div>
              <Scale className="w-10 h-10 text-orange-400" />
            </div>
            <div className="mt-4 flex items-center gap-2">
              <TrendingUp className={getTrendIconClass(weightChange)} />
              <span className="text-sm font-medium text-red-600">+{weightChange}% ({lastData.weight - firstData.weight} lbs)</span>
            </div>
            <div className="mt-2 text-sm text-gray-600">
              Since June 2021: {firstData.weight}  {lastData.weight} lbs
            </div>
          </div>

          {/* Metformin Card */}
          <div className="rounded-lg p-6 border-2 bg-blue-50 border-blue-200">
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-lg font-semibold text-gray-700">Metformin Dose</h3>
                <p className="text-3xl font-bold text-gray-900 mt-2">{lastData.metformin} mg</p>
                <p className="text-sm text-gray-600 mt-1">Daily dose</p>
              </div>
              <Pill className="w-10 h-10 text-blue-400" />
            </div>
            <div className="mt-4 flex items-center gap-2">
              <AlertCircle className="w-5 h-5 text-blue-600" />
              <span className="text-sm font-medium text-blue-700">No adjustments made</span>
            </div>
            <div className="mt-2 text-sm text-gray-600">
              Consistent dose since June 2021
            </div>
          </div>
        </div>

        {/* Main Chart */}
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">Trends Over Time</h3>

          {/* Custom Legend */}
          <div className="flex flex-wrap gap-3 mb-6">
            <div 
              onClick={() => toggleMetric('hba1c')}
              className={legendItemClass('hba1c', selectedMetrics.hba1c)}
            >
              <div className="w-4 h-4 bg-red-500 rounded"></div>
              <span>HbA1c (%)</span>
            </div>
            <div 
              onClick={() => toggleMetric('weight')}
              className={legendItemClass('weight', selectedMetrics.weight)}
            >
              <div className="w-4 h-4 bg-orange-500 rounded"></div>
              <span>Weight (lbs)</span>
            </div>
            <div 
              onClick={() => toggleMetric('metformin')}
              className={legendItemClass('metformin', selectedMetrics.metformin)}
            >
              <div className="w-4 h-4 bg-blue-500 rounded"></div>
              <span>Metformin (mg)</span>
            </div>
          </div>

          <ResponsiveContainer width="100%" height={400}>
            <ComposedChart data={rawData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
              <XAxis 
                dataKey="date" 
                tickFormatter={formatDate}
                tick={{ fontSize: 12 }}
              />
              <YAxis yAxisId="left" label={{ value: 'HbA1c (%) / Weight (lbs)', angle: -90, position: 'insideLeft' }} />
              <YAxis yAxisId="right" orientation="right" label={{ value: 'Metformin (mg)', angle: 90, position: 'insideRight' }} />
              <Tooltip content={<CustomTooltip />} />

              {/* Reference areas for HbA1c normal range */}
              <ReferenceArea yAxisId="left" y1={4} y2={5.6} fill="#10b981" fillOpacity={0.1} />
              <ReferenceLine yAxisId="left" y={5.6} stroke="#10b981" strokeDasharray="5 5" />

              {selectedMetrics.hba1c && (
                <Line
                  yAxisId="left"
                  type="monotone"
                  dataKey="hba1c"
                  stroke="#ef4444"
                  strokeWidth={3}
                  dot={{ fill: '#ef4444', strokeWidth: 2, r: 5 }}
                  name="HbA1c"
                />
              )}

              {selectedMetrics.weight && (
                <Line
                  yAxisId="left"
                  type="monotone"
                  dataKey="weight"
                  stroke="#f97316"
                  strokeWidth={3}
                  dot={{ fill: '#f97316', strokeWidth: 2, r: 5 }}
                  name="Weight"
                />
              )}

              {selectedMetrics.metformin && (
                <Bar
                  yAxisId="right"
                  dataKey="metformin"
                  fill="#3b82f6"
                  opacity={0.3}
                  name="Metformin"
                />
              )}
            </ComposedChart>
          </ResponsiveContainer>
        </div>

        {/* Key Findings */}
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <Info className="w-6 h-6 text-blue-600" />
            Key Findings & Recommendations
          </h3>
          <div className="space-y-4">
            <div className="flex gap-3 p-4 bg-yellow-50 rounded-lg border border-yellow-200">
              <AlertTriangle className="w-6 h-6 text-yellow-600 flex-shrink-0 mt-0.5" />
              <div>
                <p className="font-semibold text-gray-800">Suboptimal Glycemic Control</p>
                <p className="text-gray-600 mt-1">
                  Despite consistent metformin therapy, your HbA1c has risen from 5.7% to 6.1%, remaining above the target range of 4.0-5.6%.
                </p>
              </div>
            </div>

            <div className="flex gap-3 p-4 bg-orange-50 rounded-lg border border-orange-200">
              <TrendingUp className="w-6 h-6 text-orange-600 flex-shrink-0 mt-0.5" />
              <div>
                <p className="font-semibold text-gray-800">Strong Weight-HbA1c Correlation</p>
                <p className="text-gray-600 mt-1">
                  Your weight increased by 34 lbs (17.6%) during the same period, showing a correlation coefficient of 0.8 with HbA1c levels.
                </p>
              </div>
            </div>

            <div className="flex gap-3 p-4 bg-blue-50 rounded-lg border border-blue-200">
              <Target className="w-6 h-6 text-blue-600 flex-shrink-0 mt-0.5" />
              <div>
                <p className="font-semibold text-gray-800">Recommended Actions</p>
                <ul className="text-gray-600 mt-1 list-disc list-inside space-y-1">
                  <li>Consider increasing metformin dose to 1000mg daily if tolerated</li>
                  <li>Target 5-10% weight loss through diet and exercise</li>
                  <li>Monitor HbA1c every 3 months</li>
                  <li>Consider additional medications if HbA1c remains &gt;6%</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
