const HealthVisualization = () => {
  const [selectedMetrics, setSelectedMetrics] = useState({
    hba1c: true,
    weight: true,
    metformin: true
  });

  // Extract exact data from medical analysis
  const rawData = [
    { date: "2021-04-01", hba1c: 6.3, weight: 206.5, metformin: 0 },
    { date: "2021-06-01", hba1c: 6.3, weight: 206.5, metformin: 500 },
    { date: "2022-01-01", hba1c: 6.0, weight: 200.0, metformin: 500 },
    { date: "2023-06-01", hba1c: 5.7, weight: 192.9, metformin: 500 },
    { date: "2024-01-01", hba1c: 5.9, weight: 210.0, metformin: 500 },
    { date: "2025-04-01", hba1c: 6.1, weight: 226.6, metformin: 500 }
  ];

  // Key milestones
  const milestones = [
    { date: "2021-06-01", label: "Started Metformin 500mg", type: "medication" },
    { date: "2023-06-01", label: "Best HbA1c (5.7%)", type: "success" },
    { date: "2025-04-01", label: "Current - Weight gain concern", type: "warning" }
  ];

  // Calculate changes
  const totalWeightChange = 226.6 - 192.9;
  const hba1cChange = 6.1 - 5.7;
  const monthsOnSameDose = 46;

  // Helper functions for className computation
  const getMetricCardClass = (isGood) => {
    return 'rounded-lg p-4 border-2 ' + (isGood ? 'bg-green-50 border-green-200' : 'bg-red-50 border-red-200');
  };

  const getTrendIconClass = (isPositive) => {
    return 'w-5 h-5 ' + (isPositive ? 'text-green-600' : 'text-red-600');
  };

  const getCheckboxClass = (isChecked) => {
    return 'mr-2 ' + (isChecked ? 'text-blue-600' : 'text-gray-400');
  };

  const getTimelineClass = (type) => {
    if (type === 'medication') return 'bg-blue-100 text-blue-800 border-blue-300';
    if (type === 'success') return 'bg-green-100 text-green-800 border-green-300';
    return 'bg-yellow-100 text-yellow-800 border-yellow-300';
  };

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      const dateStr = new Date(label).toLocaleDateString('en-US', { month: 'short', year: 'numeric' });
      return (
        <div className="bg-white p-3 border rounded-lg shadow-lg">
          <p className="font-semibold text-gray-700 mb-2">{dateStr}</p>
          {payload.map((entry, index) => {
            if (!entry.value) return null;
            let unit = '';
            let label = '';
            if (entry.dataKey === 'hba1c') {
              unit = '%';
              label = 'HbA1c';
            } else if (entry.dataKey === 'weight') {
              unit = ' lbs';
              label = 'Weight';
            } else if (entry.dataKey === 'metformin') {
              unit = ' mg';
              label = 'Metformin';
            }
            return (
              <p key={index} style={{ color: entry.color }} className="text-sm">
                {label}: {entry.value}{unit}
              </p>
            );
          })}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="p-6 bg-gray-50 space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">HbA1c, Metformin, and Weight Analysis</h2>
        <p className="text-gray-600">Tracking your diabetes management from April 2021 to present</p>
      </div>

      {/* Key Findings Summary */}
      <div className="grid md:grid-cols-3 gap-4">
        <div className={getMetricCardClass(false)}>
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-semibold text-gray-700">HbA1c Trend</h3>
            <AlertTriangle className={getTrendIconClass(false)} />
          </div>
          <p className="text-2xl font-bold text-gray-800">6.1%</p>
          <p className="text-sm text-gray-600 mt-1">Current (was 5.7% at best)</p>
          <div className="mt-3 text-sm">
            <span className="text-red-600 font-medium">+0.4%</span> from best response
          </div>
        </div>

        <div className={getMetricCardClass(false)}>
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-semibold text-gray-700">Weight Change</h3>
            <Scale className={getTrendIconClass(false)} />
          </div>
          <p className="text-2xl font-bold text-gray-800">226.6 lbs</p>
          <p className="text-sm text-gray-600 mt-1">Current weight</p>
          <div className="mt-3 text-sm">
            <span className="text-red-600 font-medium">+33.7 lbs</span> from lowest (192.9 lbs)
          </div>
        </div>

        <div className="bg-yellow-50 border-2 border-yellow-200 rounded-lg p-4">
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-semibold text-gray-700">Metformin Dose</h3>
            <Pill className="w-5 h-5 text-yellow-600" />
          </div>
          <p className="text-2xl font-bold text-gray-800">500mg</p>
          <p className="text-sm text-gray-600 mt-1">Unchanged for 46 months</p>
          <div className="mt-3 text-sm">
            <span className="text-yellow-700 font-medium">25-50%</span> of typical dose
          </div>
        </div>
      </div>

      {/* Timeline Chart */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-gray-800">Timeline Analysis</h3>
          <div className="flex gap-4">
            <label className="flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={selectedMetrics.hba1c}
                onChange={(e) => setSelectedMetrics({...selectedMetrics, hba1c: e.target.checked})}
                className={getCheckboxClass(selectedMetrics.hba1c)}
              />
              <span className="text-sm text-gray-700">HbA1c</span>
            </label>
            <label className="flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={selectedMetrics.weight}
                onChange={(e) => setSelectedMetrics({...selectedMetrics, weight: e.target.checked})}
                className={getCheckboxClass(selectedMetrics.weight)}
              />
              <span className="text-sm text-gray-700">Weight</span>
            </label>
            <label className="flex items-center cursor-pointer">
              <input
                type="checkbox"
                checked={selectedMetrics.metformin}
                onChange={(e) => setSelectedMetrics({...selectedMetrics, metformin: e.target.checked})}
                className={getCheckboxClass(selectedMetrics.metformin)}
              />
              <span className="text-sm text-gray-700">Metformin</span>
            </label>
          </div>
        </div>

        <ResponsiveContainer width="100%" height={400}>
          <ComposedChart data={rawData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
            <XAxis 
              dataKey="date" 
              tickFormatter={(date) => new Date(date).toLocaleDateString('en-US', { month: 'short', year: 'numeric' })}
            />
            {selectedMetrics.hba1c && (
              <YAxis yAxisId="left" orientation="left" stroke="#dc2626" domain={[5.5, 6.5]} />
            )}
            {selectedMetrics.weight && (
              <YAxis yAxisId="right" orientation="right" stroke="#2563eb" domain={[180, 240]} />
            )}
            <Tooltip content={<CustomTooltip />} />
            <Legend />

            {/* Reference areas */}
            {selectedMetrics.hba1c && (
              <ReferenceArea yAxisId="left" y1={5.7} y2={6.5} fill="#fee2e2" fillOpacity={0.3} />
            )}

            {/* Milestones */}
            {milestones.map((milestone, index) => (
              <ReferenceLine 
                key={index}
                x={milestone.date} 
                stroke="#666" 
                strokeDasharray="5 5"
                label={{ value: milestone.label, position: "top", fontSize: 12 }}
              />
            ))}

            {/* Data lines */}
            {selectedMetrics.hba1c && (
              <Line 
                yAxisId="left"
                type="monotone" 
                dataKey="hba1c" 
                stroke="#dc2626" 
                strokeWidth={3}
                dot={{ fill: '#dc2626', r: 6 }}
                name="HbA1c (%)"
              />
            )}
            {selectedMetrics.weight && (
              <Line 
                yAxisId="right"
                type="monotone" 
                dataKey="weight" 
                stroke="#2563eb" 
                strokeWidth={3}
                dot={{ fill: '#2563eb', r: 6 }}
                name="Weight (lbs)"
              />
            )}
            {selectedMetrics.metformin && (
              <Bar 
                yAxisId="left"
                dataKey="metformin" 
                fill="#10b981" 
                opacity={0.3}
                name="Metformin (mg)"
              />
            )}
          </ComposedChart>
        </ResponsiveContainer>
      </div>

      {/* Correlation Analysis */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Key Correlations Identified</h3>
        <div className="space-y-4">
          <div className="flex items-start gap-3">
            <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
            <div>
              <p className="font-medium text-gray-800">Strong Weight-HbA1c Correlation</p>
              <p className="text-sm text-gray-600 mt-1">Every 10-pound weight change corresponds to approximately 0.2-0.3% change in HbA1c</p>
            </div>
          </div>

          <div className="flex items-start gap-3">
            <AlertCircle className="w-5 h-5 text-yellow-600 mt-0.5" />
            <div>
              <p className="font-medium text-gray-800">Subtherapeutic Dosing</p>
              <p className="text-sm text-gray-600 mt-1">You've been on only 500mg for 4 years, when most patients need 1000-2000mg for optimal effect</p>
            </div>
          </div>

          <div className="flex items-start gap-3">
            <AlertTriangle className="w-5 h-5 text-red-600 mt-0.5" />
            <div>
              <p className="font-medium text-gray-800">Treatment Gap Concerns</p>
              <p className="text-sm text-gray-600 mt-1">Multiple 3-8 month gaps between prescriptions may have contributed to worsening control</p>
            </div>
          </div>
        </div>
      </div>

      {/* Timeline Events */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Timeline of Key Events</h3>
        <div className="space-y-3">
          {milestones.map((milestone, index) => {
            const timelineClass = getTimelineClass(milestone.type);
            return (
              <div key={index} className="flex items-center gap-4">
                <div className="text-sm text-gray-600 w-24">
                  {new Date(milestone.date).toLocaleDateString('en-US', { month: 'short', year: 'numeric' })}
                </div>
                <div className={'px-3 py-1 rounded-full text-sm font-medium border ' + timelineClass}>
                  {milestone.label}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};
